import React, { createContext, useReducer, useContext, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

// ---------------- Context + Reducer ----------------
const TaskContext = createContext();

const ACTIONS = {
  ADD: "ADD",
  REMOVE: "REMOVE",
  TOGGLE: "TOGGLE"
};

function taskReducer(state, action) {
  switch (action.type) {
    case ACTIONS.ADD:
      return [...state, { id: Date.now(), text: action.payload, completed: false }];
    case ACTIONS.REMOVE:
      return state.filter(task => task.id !== action.payload);
    case ACTIONS.TOGGLE:
      return state.map(task =>
        task.id === action.payload ? { ...task, completed: !task.completed } : task
      );
    default:
      return state;
  }
}

function TaskProvider({ children }) {
  const [tasks, dispatch] = useReducer(taskReducer, [
    { id: 1, text: "DotNet Project Task", completed: false },
    { id: 2, text: "React Application Project Task", completed: true }
  ]);

  return (
    <TaskContext.Provider value={{ tasks, dispatch }}>
      {children}
    </TaskContext.Provider>
  );
}

// ---------------- Custom Hook ----------------
function useTasks() {
  const ctx = useContext(TaskContext);
  if (!ctx) throw new Error("useTasks must be used inside TaskProvider");
  const { tasks, dispatch } = ctx;

  const add = text => dispatch({ type: ACTIONS.ADD, payload: text });
  const remove = id => dispatch({ type: ACTIONS.REMOVE, payload: id });
  const toggle = id => dispatch({ type: ACTIONS.TOGGLE, payload: id });

  return { tasks, add, remove, toggle };
}

// ---------------- Components ----------------
function AddTask() {
  const [text, setText] = useState("");
  const { add } = useTasks();

  const handleSubmit = e => {
    e.preventDefault();
    if (!text.trim()) return;
    add(text);
    setText("");
  };

  return (
    <form onSubmit={handleSubmit} className="d-flex gap-2 mb-3">
      <input
        type="text"
        className="form-control"
        placeholder="Add new task..."
        value={text}
        onChange={e => setText(e.target.value)}
      />
      <button type="submit" className="btn btn-primary">
        Add
      </button>
    </form>
  );
}

function TaskItem({ task }) {
  const { toggle, remove } = useTasks();
  return (
    <li className="list-group-item d-flex justify-content-between align-items-center">
      <div>
        <input
          type="checkbox"
          className="form-check-input me-2"
          checked={task.completed}
          onChange={() => toggle(task.id)}
        />
        <span
          style={{ textDecoration: task.completed ? "line-through" : "none" }}
        >
          {task.text}
        </span>
      </div>
      <button className="btn btn-sm btn-danger" onClick={() => remove(task.id)}>
        Remove
      </button>
    </li>
  );
}

function TaskList() {
  const { tasks } = useTasks();
  const total = tasks.length;
  const completed = tasks.filter(t => t.completed).length;

  return (
    <div>
      <h5 className="mb-3">
        Total Tasks:{" "}
        <span className="badge bg-secondary">{total}</span> | Completed:{" "}
        <span className="badge bg-success">{completed}</span>
      </h5>
      {tasks.length === 0 ? (
        <p className="text-muted">No tasks yet. Add one above!</p>
      ) : (
        <ul className="list-group">
          {tasks.map(task => (
            <TaskItem key={task.id} task={task} />
          ))}
        </ul>
      )}
    </div>
  );
}

// ---------------- Main App ----------------
function App() {
  return (
    <TaskProvider>
      <div className="App">
  <header className="App-header">
    <h1>Task Management App</h1>
    <p>Total Tasks: {tasks.length} | Completed: {completedTasks}</p>
  </header>

  <div className="task-list">
    <form onSubmit={addTask} className="d-flex mb-3">
      <input 
        type="text" 
        value={newTask} 
        onChange={(e) => setNewTask(e.target.value)} 
        className="form-control me-2"
        placeholder="Add new task..."
      />
      <button type="submit" className="btn btn-custom">Add</button>
    </form>

    {tasks.length === 0 ? (
      <p className="text-muted">No tasks yet — add one!</p>
    ) : (
      tasks.map((task, index) => (
        <div 
          key={index} 
          className={`task-item ${task.completed ? "task-completed" : ""}`}
          onClick={() => toggleTask(index)}
        >
          {task.title}
        </div>
      ))
    )}
  </div>
</div>

    </TaskProvider>
  );
}

export default App;


